import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://localhost:5000/product/${id}`);
        if (!response.ok) throw new Error("Failed to fetch product");
        const data = await response.json();
        setProduct(data);
        setError(null);
      } catch (err) {
        setError(err.message);
        console.error("Error fetching product:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    console.log(`Added ${quantity} of ${product.name} to cart`);
    // Add your cart logic here
  };

  if (loading) {
    return (
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-md-8">
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="mt-3">Loading product details...</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-md-6">
            <div className="alert alert-danger">
              <h4 className="alert-heading">Error!</h4>
              <p>{error || "Product not found"}</p>
              <hr />
              <Link to="/" className="btn btn-outline-danger">
                ← Back to Products
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-5">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/" className="text-decoration-none">
              Home
            </Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            {product.name}
          </li>
        </ol>
      </nav>

      <div className="row">
        {/* Product Image */}
        <div className="col-md-6 mb-4">
          <div className="card shadow-sm">
            <div className="card-body p-4">
              <img
                src={`http://localhost:5000/${product.image_path}`}
                alt={product.name}
                className="img-fluid rounded"
                style={{ maxHeight: "500px", objectFit: "contain" }}
              />
            </div>
          </div>
        </div>

        {/* Product Details */}
        <div className="col-md-6">
          <div className="card shadow-sm">
            <div className="card-body p-4">
              {/* Product Name */}
              <h1 className="h2 mb-3">{product.name}</h1>

              {/* Rating */}
              <div className="mb-4">
                <div className="text-warning mb-1">
                  {[...Array(5)].map((_, i) => (
                    <i
                      key={i}
                      className={`bi ${
                        i < 4 ? "bi-star-fill" : "bi-star-half"
                      }`}
                    ></i>
                  ))}
                </div>
                <span className="text-muted">(128 reviews)</span>
              </div>

              {/* Price */}
              <div className="mb-4">
                <h2 className="h3 text-primary mb-2">
                  {product.price} DH
                </h2>
              </div>

              {/* Description */}
              <div className="mb-4">
                <h5 className="mb-3">Description</h5>
                <p className="text-muted">{product.description}</p>
              </div>

              {/* Quantity Selector */}
              <div className="mb-4">
                <h5 className="mb-3">Quantity</h5>
                <div className="input-group" style={{ width: "150px" }}>
                  <button
                    className="btn btn-outline-secondary"
                    type="button"
                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  >
                    −
                  </button>
                  <input
                    type="text"
                    className="form-control text-center"
                    value={quantity}
                    readOnly
                  />
                  <button
                    className="btn btn-outline-secondary"
                    type="button"
                    onClick={() => setQuantity(quantity + 1)}
                  >
                    +
                  </button>
                </div>
              </div>

              {/* Add to Cart Button */}
              <div className="mb-4">
                <button
                  className="btn btn-primary btn-lg w-100 py-3"
                  onClick={handleAddToCart}
                >
                  <i className="bi bi-cart-plus me-2"></i>
                  Add to Cart
                </button>
              </div>

              {/* Product Features */}
              <div className="row g-3 mb-4">
                <div className="col-6">
                  <div className="d-flex align-items-center">
                    <i className="bi bi-truck text-primary fs-4 me-3"></i>
                    <div>
                      <small className="text-muted d-block">Shipping</small>
                      <strong>Free Delivery</strong>
                    </div>
                  </div>
                </div>
                <div className="col-6">
                  <div className="d-flex align-items-center">
                    <i className="bi bi-shield-check text-success fs-4 me-3"></i>
                    <div>
                      <small className="text-muted d-block">Warranty</small>
                      <strong>1 Year</strong>
                    </div>
                  </div>
                </div>
              </div>

              {/* Stock Status */}
              <div className="alert alert-success">
                <div className="d-flex justify-content-between align-items-center">
                  <span>
                    <i className="bi bi-check-circle me-2"></i>
                    <strong>In Stock</strong>
                  </span>
                  <span className="text-muted">12 items left</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Additional Info */}
      <div className="row mt-5">
        <div className="col-12">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title mb-4">Product Information</h5>
              <div className="row">
                <div className="col-md-6">
                  <ul className="list-unstyled">
                    <li className="mb-2">
                      <strong>Category:</strong> Electronics
                    </li>
                    <li className="mb-2">
                      <strong>Brand:</strong> Premium Brand
                    </li>
                    <li className="mb-2">
                      <strong>SKU:</strong> PRD-{id}
                    </li>
                  </ul>
                </div>
                <div className="col-md-6">
                  <ul className="list-unstyled">
                    <li className="mb-2">
                      <strong>Weight:</strong> 1.2 kg
                    </li>
                    <li className="mb-2">
                      <strong>Dimensions:</strong> 10 x 15 x 8 cm
                    </li>
                    <li className="mb-2">
                      <strong>Return Policy:</strong> 30 days return
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Back Button */}
      <div className="mt-4">
        <Link to="/products" className="btn btn-outline-secondary">
          <i className="bi bi-arrow-left me-2"></i>
          Back to Products
        </Link>
      </div>
    </div>
  );
};

export default ProductDetails;
